          gymExerciseDatabase.abs[0],
          gymExerciseDatabase.abs[1],
        ],
      },
    ],
  },
];

export function selectPlanBasedOnFrequency(
  weeklyFrequency: 2 | 3 | 4 | 5 | 6,
  goal: "hypertrophy" | "weight_loss" | "endurance" | "beginner"
): "ABC" | "ABCD" | "FullBody" | "UpperLower" {
  switch (weeklyFrequency) {
    case 2:
      return "FullBody";
    case 3:
      return "ABC";
    case 4:
      if (goal === "hypertrophy" || goal === "beginner") {
        return "ABCD";
      }
      return "UpperLower";
    case 5:
      return "ABCD";
    case 6:
      return "UpperLower";
    default:
      return "FullBody";
  }
}

export function generateWorkout(user: User): Exercise[] {
  const weeklyFrequency = user.weeklyFrequency || 3;
  const goal = user.goal || 'beginner';

  const selectedPlanType = selectPlanBasedOnFrequency(weeklyFrequency, goal);

  const selectedWorkoutPlan = workoutPlans.find(plan => plan.type === selectedPlanType);

  if (!selectedWorkoutPlan || !selectedWorkoutPlan.workouts[0]) {
    return [];
  }

  const firstWorkout = selectedWorkoutPlan.workouts[0];
  const gymExercises = firstWorkout.exercises;

  return gymExercises.map((ex, idx) => ({
    id: `${Date.now()}-${idx}`,
    name: ex.name,
    sets: ex.sets,
    reps: typeof ex.reps === 'string' ? parseInt(ex.reps.split('-')[0]) : ex.reps,
    rest: ex.rest,
    completed: false,
    muscleGroup: getMuscleGroupLabel(ex.muscleGroup),
  }));
}

function getMuscleGroupLabel(muscleGroup: MuscleGroup): string {
  const labels: Record<MuscleGroup, string> = {
    chest: 'Peito',
    back: 'Costas',
    legs: 'Pernas',
    shoulders: 'Ombros',
    biceps: 'Bíceps',
    triceps: 'Tríceps',
    abs: 'Abdômen',
  };
  return labels[muscleGroup] || 'Geral';
}

export function calculateWorkoutPoints(
  duration: number,
  exerciseCount: number,
  streak: number,
  level: "beginner" | "intermediate" | "advanced"
): number {
  const levelMultiplier = level === "beginner" ? 1 : level === "intermediate" ? 1.3 : 1.6;
  const streakMultiplier = 1 + (streak * 0.05);

  const basePoints = (duration * levelMultiplier) + (exerciseCount * 50);
  const totalPoints = Math.round(basePoints * streakMultiplier);

  return totalPoints;
}

export function estimateCalories(
  duration: number,
  level: "beginner" | "intermediate" | "advanced",
  weight: number
): number {
  const baseRate = level === "beginner" ? 5 : level === "intermediate" ? 7 : 9;
  return Math.round((duration * baseRate * weight) / 70);
}

export { gymExerciseDatabase, workoutPlans };
export type { GymExercise, WorkoutPlan, MuscleGroup };

